    //             sum += napoj.cena;
    //         });
    //         setTotalPrice(sum);
    //     }
    // }, [koszyk]);


    if (!koszyk) {
        return <div>Loading...</div>;
    }

    return (
        <center>
        <div>
            <h2>Koszyk</h2>

            <ul>
            <li>{koszyk.id}</li>
            <li>{koszyk.pizzas}</li>
            </ul>

        </div>
        </center>
    );
}

export default Koszyk;
