import React, { useState, useEffect } from 'react';
import { BrowserRouter as Link } from "react-router-dom";
import axios from 'axios';
//import { Link } from 'react-router-dom';
import './style/Menu.css';

function PizzaMenu({ user }) {
    const [pizzas, setPizzas] = useState([]);

    useEffect(() => {
        // Pobierz menu pizz z backendu
        axios.get('/api/pizzas')
            .then(response => setPizzas(response.data))
            .catch(error => console.error(error));
    }, []);

    return (
        <center>
        <div>
        <h2>Menu Pizz</h2>
        <table className="menu-table">
            <thead>
                <tr>
                    <th>Nazwa</th>
                    <th>Cena</th>
                    <th>Rozmiar</th>
                    <th>Wegetariańska</th>
                    <th>Czy Dostępna</th>
                </tr>
            </thead>
            <tbody>
                {pizzas.map(pizza => (
                    <tr key={pizza.id}>
                        <td>{pizza.nazwa}</td>
                        <td>{pizza.cena} PLN</td>
                        <td>{pizza.rozmiar}</td>
                        <td>{pizza.czyWegetarianska ? 'Tak' : 'Nie'}</td>
                        <td>{pizza.czyDostepna ? 'Tak' : 'Nie'}</td>
                    </tr>
                ))}
            </tbody>
        </table>
        {user && user.username === 'admin' && <Link to="/addpizza" class="menuButton">Dodaj Pizze</Link>}
    </div>
    </center>
);
};
    
    export default PizzaMenu;
