import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Koszyk({ user }) {
    const [koszyk, setKoszyk] = useState(null);
    const [totalPrice, setTotalPrice] = useState(0);

    useEffect(() => {
        const fetchKoszyk = async () => {
            try {
                const response = await axios.post(`/api/users/koszyk?username=${user.username}`);
                setKoszyk(response.data);
            } catch (error) {
                console.error('Error fetching koszyk:', error);
            }
        };

        fetchKoszyk();
    }, [user]);

    // useEffect(() => {
    //     if (koszyk) {
    //         let sum = 0;
    //         koszyk.pizzas.forEach(pizza => {
    //             sum += pizza.cena;
    //         });
    //         koszyk.napoje.forEach(napoj => {
    //             sum += napoj.cena;
    //         });
    //         setTotalPrice(sum);
    //     }
    // }, [koszyk]);


    return (
        <div>
            <h2>Koszyk</h2>

            <ul>
                {koszyk.pizzas.map(pizza => (
                    <li key={pizza.id}>{pizza.nazwa} - Cena: {pizza.cena} PLN</li>
                ))}
            </ul>

            <ul>
                {koszyk.napoje.map(napoj => (
                    <li key={napoj.id}>{napoj.nazwa} - Cena: {napoj.cena} PLN</li>
                ))}
            </ul>
            {/* <h3>Suma Koszyka: {totalPrice} PLN</h3> */}
        </div>
    );
}

export default Koszyk;
