import './App.css';
import styled from 'styled-components';
const Title = styled.h1 `
font-size: 1em; margin: 1em;
padding: 0.25em 1em; border:
2px solid palevioletred;
border-radius: 3px;
`; 

const HeaderWrapper = styled.header`
  background-color: #333;
  color: white;
  padding: 1em;
`;

const Nav = styled.nav`
  display: flex;
  justify-content: space-between;
  align-items: center;
`;



function Hello() {
  return (
   <div>
   <h1>Hello World!</h1>
   </div>
   );
  } 

  function Hello2({ title, content }) { return (
    <div>
    <h1>{title}</h1>
    <p>{content}</p>
    </div>
    );
   } 


   function ContactsHeader()
{ return ( <HeaderWrapper>
 <Nav>
 <div>
          <h1>Lista kontaktów</h1>
  </div>
 <div>
 <button>Dodaj</button>
 </div>
 </Nav>
 </HeaderWrapper>
 );
} 

function ContactItem({ login, name, department }) { return ( <li>
  <div>
  <h4>{name}</h4>
  <div>{department}</div>
  </div>
  </li >
  );
 } 

 function ContactsList() {
  return (
   <ul>
   <ContactItem login="Kontakt1" name="Jarek" department="JavaScript Developer" />
   <ContactItem login="Kontak2" name="Marek" department="Human Resources" />
   <ContactItem login="Kontakt3" name="Czarek" department="QA" />
   </ul>
   );
  } 


  const Contacts = () => {
    return (
      <div>
        <ContactsHeader />
        <ContactsList />
      </div>
    );
  };




function App() {
  return (
    <div className="App">
      <Hello2 title="Cześć kolego" content="Jan kowalski"></Hello2> 
      <Contacts></Contacts>
    </div>
  );
}

export default App;
