import React, { useState } from 'react';
import { useNavigate  } from 'react-router-dom';
import axios from 'axios';

const Login = ({ onLogin }) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleLogin = async () => {
        try {
            const response = await axios.post('/api/users/login', { username, password });
            console.log('Logged in user:', response.data);
            onLogin(response.data);
            // Dodaj odpowiednią obsługę sukcesu (np. przekierowanie na stronę główną)
            navigate('/login');
        } catch (error) {
            console.error('Error during login:', error);
            // Dodaj odpowiednią obsługę błędów (np. wyświetlanie komunikatu)
        }
    };

    return (
        <center>
        <div>
            <h2>Logowanie</h2>
            <label>Username: <input type="text" onChange={(e) => setUsername(e.target.value)} /></label>
            <label>Password: <input type="password" onChange={(e) => setPassword(e.target.value)} /></label>
            <button onClick={handleLogin}>Zaloguj</button>
        </div>
        </center>
    );
};

export default Login;
